import Vue from "vue";
import Vuex from "vuex"

Vue.use(Vuex)

const storage = {
    fetch() {
        if (localStorage.length > 0) {
            for (let i = 0; i < localStorage.length; i++) {
              this.todoItems.push(JSON.parse(localStorage.getItem(localStorage.key(i))));
            }
          }
    },
}

export const store = new Vuex.Store({
    state: {
        todoItems: []
    }
})
